func wordBreak(s string, wordDict []string) bool {

	wordMap := make(map[string]bool)
	for _, str := range wordDict {
		wordMap[str] = true
	}

	juzhen := make([][]bool, len(s)+1)
	for i := 0; i < len(s)+1; i++ {
		juzhen[i] = make([]bool, len(s)+1)
	}

	var isRight func(int, int) bool
	isRight = func(l, r int) bool { // 左闭右开
		if l == r ||  juzhen[l][r]{
			return true
		}
		if wordMap[s[l:r]] == true {
			juzhen[l][r] = true
			return true
		}
		for i := l+1 ; i < r; i++ {
			if isRight(l, i) && isRight(i, r) {
				juzhen[l][r] = true
				break
			}
		}
		return juzhen[l][r]
	}

	return isRight(0, len(s))
}

