func wordBreak2(s string, wordDict []string) bool {

	hash := make(map[string]bool)
	for _, str := range wordDict {
		hash[str] = true
	}

	dp := make([]bool, len(s)+1)
	dp[0] = true
	//for i:=0;i<len(s);i++{
	//	for j:=i+1;j<len(s)+1;j++{
	//		if dp[i] && hash[s[i:j]] == true {
	//			dp[j] = true
	//		}
	//	}
	//}
	for i := 1; i <= len(s); i++ {
		for j := 0; j < i; j++ {
			if dp[j] && hash[s[j:i]] == true {
				dp[i] = true
				break
			}
		}
	}

	return dp[len(s)]
}

